import React, { useState, useEffect } from "react"
import {
  Container,
  Row,
  Col,
  Table,
  Input,
  Collapse,
  Card,
  Form,
  FormGroup,
  Label,
  CardBody,
  Media
} from "reactstrap"
import { Link } from "react-router-dom"
import SweetAlert from "react-bootstrap-sweetalert"
//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import axios from "axios"
import { useHistory } from 'react-router'
import styled from "styled-components"
const LoadingBox = styled.div`
width: 100%;
align-items: center;
display: flex;
flex-direction: column;
`
const CustomerRevise = () => {
  const [isOpen, setIsOpen] = useState(true);
  const toggle = () => setIsOpen(!isOpen);
  const [with_save, setwith_save] = useState(false);
  const [with_cancel, setwith_cancel] = useState(false);
  const [with_good, setwith_good] = useState(false);
  const [loading, setLoading] = useState(false);
  const [id, setId] = useState('');
  const [pw, setPw] = useState('');
  const [checkId, setCheckId] = useState('');
  const [checkPw, setCheckPw] = useState(false);
  const [checkAddUser, setCheckAddUser] = useState(false);
  const [userLevel, setUserLevel] = useState(0);
  const selectList = ["일반유저", "관리자", "개발자"];
  const [selected, setSelected] = useState("일반유저");
  const isAdmin = async () => {
    setLoading(true);
    const { data: response } = await axios.get('/api/auth')
    if(!response.third){
      alert('회원만 접근 가능합니다.')
      history.push('/login')
    }
    else{

        if(!response.first){
          alert('개발자만 접근 가능합니다.')
          history.push('/product-list')
        }else{
          setLoading(false)
        }

    } 
  }
  useEffect(() => {
    isAdmin()
  }, [])
  const handleSelect = (e) => {
    setSelected(e.target.value);
  };

  const history = useHistory()


  const onSubmit = () => {

    // if (checkAddUser) {
      //회원가입
      axios.post('/api/adduser', {
         id: id, 
         pw: pw,
        userLevel: userLevel 
        }).then(()=>{
          console.log("success")
          setwith_save(false)
           setwith_good(true)
          history.push('/customer-list')
        })
        .catch(err => console.log(err))

    // } else {
    //   if(checkId===''){
    //     alert('ID 중복확인을 해주세요.')
    //   }
    //   else{
    //     alert('필수정보가 입력되지 않았습니다.')
    //   }
    //   setwith_save(false)
    // }
  };

  useEffect(() => {
    if (
      id.length === 0 ||
      pw.length === 0 ||
      checkId !== '사용 가능한 ID입니다.'
    ) {
      setCheckAddUser(false)

    } else {
      setCheckAddUser(true)
    }
  })

 useEffect(()=>{
  if(selected==='일반유저') setUserLevel(0)
    else if(selected==='관리자') setUserLevel(40)
    else{
      setUserLevel(50)
    }
