import React, { useEffect, useState } from "react";
import { Col, Container, Row, Card, Spinner } from "reactstrap"
import { Link } from "react-router-dom"
//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import axios from 'axios'
import styled from "styled-components"
import SweetAlert from "react-bootstrap-sweetalert"
import { useHistory } from 'react-router';
import { AvForm } from "availity-reactstrap-validation"
const CheckBox = styled.input`
margin: 14px 14px 14px;
`
const LoadingBox = styled.div`
width: 100%;
align-items: center;
display: flex;
flex-direction: column;
`
const Table = styled.table`
  width: 100%;
  background:#ffffff;
  margin-bottom: 14px;
  align-items: center;
  display:flex;
  justify-content: space-between;
  box-shadow: 1px 1px 1px #00000029;
  word-break:break-all;
  th{
    color: #596275;
  }
`
const BrandName = styled.th`
  width: 15%;
  color: black;
  padding: 14px 14px 14px;
  text-align:center;
`
const Class1 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;
  text-align:center;
`
const Class2 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;
  text-align:center;
`
const Class3 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;
  text-align:center;
`
const Class4 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;
  text-align:center;
`
const Status = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;
  text-align:center;
`
const Date = styled.th`
  width: 15%;
  color: black;
  padding: 14px 14px 14px;
  text-align:center;
`
const Modify = styled.th`
width: 7.5%;
  color: black;
  padding: 14px 14px 14px;
`
const Delete = styled.th`
width: 7.5%;
  color: black;
  padding: 14px 14px 14px;
`
const ListText = styled.p`
font-weight: 400;
margin:0;
`
const PageBox = styled.div`
width: 100%;
align-items: center;
display: flex;
flex-direction: column;
`
const PageUl = styled.ul`
  float:left;
  list-style: none;
  text-align:center;
  border-radius:3px;
  color:black;
  padding:1px;

  background-color: white;
`;

const PageLi = styled.li`
  display:inline-block;
  font-size:17px;
  font-weight:600;
  padding:5px;


  &:hover{
    cursor:pointer;
    color:white;
    background-color:blue;
  }
  &:focus::after{
    color:#ffffff;
    background-color:#000000;
  }
`;

const PageSpan = styled.span`
  &:hover::after,
  &:focus::after{
    border-radius:100%;
    color:black;
    background-color:black;
  }
`;

const BrandList = () => {

  const history = useHistory()
  const [deleteNum, setDeleteNum] = useState(0)
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [maxPage, setMaxPage] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [with_delete, setwith_delete] = useState(false);
  const [search, setSearch] = useState('')
  const [myPk, setMyPk] = useState(0)
  const isAdmin = async () => {
    setLoading(true);
    const { data: response } = await axios.get('/api/auth')

    if (!response.third) {
      alert('회원만 접근 가능합니다.')
      history.push('/login')
    }
    else {
      if (!response.second) {
        alert('관리자만 접근 가능합니다.')
        history.push('/product-list')
      } else {
        setMyPk(response.pk)
        const page = currentPage
        const { data: response2 } = await axios.get(`/api/brand?page=${page}&keyword=${search}&pk=${response.pk}`);
        console.log(response2)
        setPosts(response2.data.result);
        setMaxPage(response2.data.maxPage);
        setLoading(false)
      }
    }
  }
  useEffect(() => {
    isAdmin()
  }, [])


  function setStatus(stt) {
    if (stt === 1) {
      return "사용중"
    }
    else {
      return "사용안함"
    }
  }
  const onSearch = (e) => {
    setSearch(e.target.value)
  }
  function onChangePage(num) {
    async function fetchPosts() {
      setLoading(true);
      const page = num
      const { data: response } = await axios.get(`/api/brand?page=${page}&keyword=${search}&pk=${myPk}`);
      setPosts(response.data.result);
      setLoading(false);
    }
    fetchPosts()
  };
  function onCreateTime(num) {
    num = num.split('.')[0]
    num = num.replace('T', ' ')
    return num
  }
  const pageNumbers = [];
  for (let i = 1; i <= maxPage; i++) {
    pageNumbers.push(i);
  }

  return (
    <React.Fragment>
      <div className="page-content" style={{color:'#596275'}}>
        <Container fluid style={{fontFamily:'NanumGothic'}}>
          <Breadcrumbs breadcrumbItem="브랜드관리" />
          <Row>
            <Col lg="12">

              <React.Fragment>
                <div>
                  <Card>
                    <AvForm className="app-search d-none d-lg-block" onSubmit={() => { onChangePage(1) }}>
                      <div className="position-relative">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Search..."
                          value={search}
                          onChange={onSearch}
