import React, { useEffect, useState } from "react";
import { Button, Col, Container, Row, Label, Input, Card, Spinner } from "reactstrap"
import { Link } from "react-router-dom"
//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import axios from 'axios'
import styled from "styled-components"
import SweetAlert from "react-bootstrap-sweetalert"
import { padStart } from "lodash";
import {useHistory, useLocation} from 'react-router';

const CheckBox = styled.input`
margin: 14px 14px 14px;
`
const LoadingBox = styled.div`
width: 100%;
align-items: center;
display: flex;
flex-direction: column;
`
const Table = styled.table`
  width: 100%;
  background:#ffffff;
  margin-bottom: 14px;
  align-items: center;
  display:flex;
  justify-content: space-between;
  box-shadow: 1px 1px 1px #00000029;
  word-break:break-all;
`
const BrandName = styled.th`
  width: 15%;
  color: black;
  padding: 14px 14px 14px;

`
const Class1 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;

`
const Class2 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;

`
const Class3 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;

`
const Class4 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;

`
const Class5 = styled.th`
  width: 11%;
  color: black;
  padding: 14px 14px 14px;

`
const Date = styled.th`
  width: 15%;
  color: black;
  padding: 14px 14px 14px;

`
const Modify = styled.th`
width: 7.5%;
  color: black;
  padding: 14px 14px 14px;
`
const Delete = styled.th`
width: 7.5%;
  color: black;
  padding: 14px 14px 14px;
`
const ListText = styled.p`
font-weight: 400;
margin:0;
`
const PageBox = styled.div`
width: 100%;
align-items: center;
display: flex;
flex-direction: column;
`
const PageUl = styled.ul`
  float:left;
  list-style: none;
  text-align:center;
  border-radius:3px;
  color:black;
  padding:1px;

  background-color: white;
`;

const PageLi = styled.li`
  display:inline-block;
  font-size:17px;
  font-weight:600;
  padding:5px;


  &:hover{
    cursor:pointer;
    color:white;
    background-color:blue;
  }
  &:focus::after{
    color:#ffffff;
    background-color:#000000;
  }
`;

const PageSpan = styled.span`
  &:hover::after,
  &:focus::after{
    border-radius:100%;
    color:black;
    background-color:black;
  }
`;

const BrandList = () => {

  const history = useHistory()
  const [deleteNum, setDeleteNum] = useState(0)
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
